package java_basics;

public class ArithmeticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1 = 200;
		int num2 = 300;
		int num3 = num1 + num2;
		int num4 = num1 / num2;
		int num5 = num1 % num2;
		System.out.println(num3);
		System.out.println(num4);
		System.out.println(num5);
		
		int num6= 1;
		num6++;
		num6--;
		num6+=5;
		num6-=10;
		num6*=2;
		num6/=5;
		System.out.println(num6);
		
	}

}
