package java_basics;

public class IfCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1 = 100;
		int num2 = 200;
		
		if (num1 > num2) {
			System.out.println("num1 is bigger");
		}
		else {
			System.out.println("num1 is not bigger");
		}
		
	}
}
