package java_basics;

public class LogicalAndBitwiseOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean a = true;
		boolean b = false;
		System.out.println(a && b);
		System.out.println(a || b);
		System.out.println(!a);
		
		int num1 = 60;
		int num2 = 42;
		System.out.println(num1 & num2);
		System.out.println(num1 | num2);
		System.out.println(~num1);
		
	}

}
